mapper.select:
 
		SELECT USERNO, USERTYPE, USERID, USERPWD, USERADDR, USEREMAIL, USERPHONE, USERNICK
		FROM BS_USER
		WHERE USERID=#{userId} AND USERPWD=#{userPwd}

SELECT USERID, USERPWD, USERADDR, USEREMAIL, USERPHONE, USERNICK
FROM BS_USER
WHERE USERNO=#{userNo}

 
		SELECT BABYNO,USERNO, BABYGENDER, TO_CHAR(BABYBIRTH,'YYYY-MM-DD') AS BABYBIRTH, BABYNAME, BABYBLOOD
		FROM BS_BABY
		WHERE BABYNO=#{babyNo}

SELECT COUNT(USERID)
FROM BS_USER
WHERE USERID=#{userId}

SELECT USERNO
FROM BS_USER
WHERE USERID=#{userId}
