mapper.update:
 
		UPDATE 
  			flea_reply
		SET
			replyText=#{replyText}
		WHERE
			fleaNum=#{fleaNum}
		and
			replyNum=#{replyNum}
		and
			userNo=#{userNo}

mapper.delete:

 		 DELETE 
 		 	flea_reply
 		 WHERE
 		 	FLEANUM=#{fleaNum}
 		 and
 		 	replyNum=#{replyNum}
 		 and
 		 	userNo=#{userNo}
